#include <string>
using namespace std;
class Solution {
public:
    bool isSubsequence(string s, string t) {
        int len1=s.size();
        int len2=t.size();
        int i=0,j=0;
        int find=0;
        if(len1==0)
            return true;
        for(i;i<len1;i++)
        {
            for(;j<len2;j++)
            {
                if(s[i]==t[j])
                {
                    find=1;
					j++;
                    break;
                }
            }
            if(find==0)
                return false;
            find=0;
        }
        return true;
    }
};

int main()
{
	Solution so;
	string str1="leeeeetcode";
	string str2="";
	int ans=0;
	ans=so.isSubsequence(str1,str2)?1:0;
	cout<<ans<<endl;
}